// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";

/**
 * @title BridgeEVM
 * @dev Cross-chain bridge contract for Sepolia network
 * @notice This contract handles token releases triggered by Solana lock events
 */
contract BridgeEVM is Ownable, ReentrancyGuard {
    // State variables
    IERC20 public immutable bridgeToken;
    uint256 public totalReleased;
    mapping(bytes32 => bool) public processedTxs;
    
    // Events
    event Released(
        address indexed recipient,
        uint256 amount,
        bytes32 indexed solanaTxHash,
        uint256 timestamp
    );
    
    event TokensDeposited(
        address indexed depositor,
        uint256 amount,
        uint256 timestamp
    );
    
    event EmergencyWithdraw(
        address indexed owner,
        uint256 amount,
        uint256 timestamp
    );

    // Custom errors
    error InsufficientBalance();
    error TransactionAlreadyProcessed();
    error InvalidAmount();
    error InvalidRecipient();
    error TransferFailed();

    /**
     * @dev Constructor
     * @param _bridgeToken Address of the ERC20 token to be used for bridging
     * @param _initialOwner Address of the initial owner (admin)
     */
    constructor(
        address _bridgeToken,
        address _initialOwner
    ) Ownable(_initialOwner) {
        require(_bridgeToken != address(0), "Invalid token address");
        require(_initialOwner != address(0), "Invalid owner address");
        
        bridgeToken = IERC20(_bridgeToken);
    }

    /**
     * @dev Release tokens to recipient (called by indexer)
     * @param recipient Address to receive tokens on Sepolia
     * @param amount Amount of tokens to release
     * @param solanaTxHash Hash of the Solana transaction that triggered this release
     */
    function release(
        address recipient,
        uint256 amount,
        bytes32 solanaTxHash
    ) external onlyOwner nonReentrant {
        // Input validation
        if (recipient == address(0)) revert InvalidRecipient();
        if (amount == 0) revert InvalidAmount();
        if (processedTxs[solanaTxHash]) revert TransactionAlreadyProcessed();

        // Check contract balance
        uint256 contractBalance = bridgeToken.balanceOf(address(this));
        if (contractBalance < amount) revert InsufficientBalance();

        // Mark transaction as processed
        processedTxs[solanaTxHash] = true;
        
        // Update total released
        totalReleased += amount;

        // Transfer tokens to recipient
        bool success = bridgeToken.transfer(recipient, amount);
        if (!success) revert TransferFailed();

        emit Released(recipient, amount, solanaTxHash, block.timestamp);
    }

    /**
     * @dev Deposit tokens to the bridge contract
     * @param amount Amount of tokens to deposit
     */
    function depositTokens(uint256 amount) external {
        if (amount == 0) revert InvalidAmount();

        bool success = bridgeToken.transferFrom(msg.sender, address(this), amount);
        if (!success) revert TransferFailed();

        emit TokensDeposited(msg.sender, amount, block.timestamp);
    }

    /**
     * @dev Emergency withdraw function for owner
     * @param amount Amount to withdraw
     */
    function emergencyWithdraw(uint256 amount) external onlyOwner {
        if (amount == 0) revert InvalidAmount();
        
        uint256 contractBalance = bridgeToken.balanceOf(address(this));
        if (contractBalance < amount) revert InsufficientBalance();

        bool success = bridgeToken.transfer(owner(), amount);
        if (!success) revert TransferFailed();

        emit EmergencyWithdraw(owner(), amount, block.timestamp);
    }

    /**
     * @dev Withdraw native ETH (in case it's sent accidentally)
     */
    function withdrawETH() external onlyOwner {
        uint256 balance = address(this).balance;
        if (balance > 0) {
            (bool success, ) = payable(owner()).call{value: balance}("");
            require(success, "ETH transfer failed");
        }
    }

    // View functions
    
    /**
     * @dev Get contract token balance
     */
    function getContractBalance() external view returns (uint256) {
        return bridgeToken.balanceOf(address(this));
    }

    /**
     * @dev Check if a Solana transaction has been processed
     */
    function isTxProcessed(bytes32 solanaTxHash) external view returns (bool) {
        return processedTxs[solanaTxHash];
    }

    /**
     * @dev Get bridge statistics
     */
    function getBridgeStats() external view returns (
        uint256 contractBalance,
        uint256 totalReleasedAmount,
        address tokenAddress,
        address bridgeOwner
    ) {
        return (
            bridgeToken.balanceOf(address(this)),
            totalReleased,
            address(bridgeToken),
            owner()
        );
    }
}

